mapper.select:
SELECT COUNT(*)
  FROM USERS
 WHERE USER_ID = #{user_id}

SELECT COUNT(*)
  FROM USERS
 WHERE USER_ID = #{user_id}
   AND USER_PASSWORD = #{user_password}

SELECT *
  FROM USERS
 WHERE USER_ID = #{user_id}
   AND USER_PASSWORD = #{user_password}

SELECT COUNT(*)
  FROM USERS
 WHERE USER_ID = #{user_id}
   AND USER_PASSWORD = #{user_password}

SELECT COUNT(*)
  FROM USERS
 WHERE USER_NAME = #{user_name}
   AND PHONE1 = #{phone1}
   AND PHONE2 = #{phone2}
   AND PHONE3 = #{phone3}

SELECT USER_ID
  FROM USERS
 WHERE USER_NAME = #{user_name}
   AND PHONE1 = #{phone1}
   AND PHONE2 = #{phone2}
   AND PHONE3 = #{phone3}

SELECT USER_REGISTRATION_TIME
  FROM USERS
 WHERE USER_ID = #{user_id}

SELECT USER_ID
  FROM USERS
 WHERE USER_NAME = #{user_name}
   AND EMAIL_ID = #{email_id}
   AND EMAIL_ADDRESS = #{email_address}

SELECT COUNT(*)
  FROM USERS
 WHERE USER_NAME = #{user_name}
   AND EMAIL_ID = #{email_id}
   AND EMAIL_ADDRESS = #{email_address}

mapper.update:
UPDATE USERS
   SET USER_PASSWORD = #{user_password}
 WHERE USER_ID = #{user_id}
