mapper.insert:
INSERT INTO NOTICE (
	NOTICE_NO, NOTICE_TYPE, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_CNT, NOTICE_TIME
) VALUES(
	#{notice_no}, 
	#{notice_type}, 
	#{notice_title}, 
	#{notice_content}, 
	#{notice_cnt},
	#{notice_time}
)

mapper.update:
UPDATE NOTICE SET
NOTICE_CNT = MAX(#{notice_cnt})+1
WHERE NOTICE_NO = #{notice_no}
